SET NAMES UTF8;
DROP DATABASE IF EXISTS kugousql;
create DATABASE kugousql CHARSET=UTF8;
USE kugousql;
-- 网站的基本信息
CREATE TABLE kg_site_info(
  site_name VARCHAR(32),
  logo VARCHAR(64),
  copyrightt VARCHAR(64)
);


-- 导航条目
CREATE TABLE kg_navbar_item(
  name VARCHAR(16),
  url VARCHAR(64),
  title VARCHAR(32)
);

-- 用户表
CREATE TABLE kg_user(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(32),
  upwd VARCHAR (32),
  email VARCHAR(64),
  phone VARCHAR(11),
  avatar VARCHAR(128),
  user_name VARCHAR(32),
  isvip VARCHAR(8)
);


-- 轮播图
CREATE TABLE kg_carousel_item(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  pic VARCHAR(128),
  url VARCHAR(128),
  title VARCHAR(64)
);


-- 精选歌单
CREATE TABLE kg_jxgd(
  id INT PRIMARY key,
  img VARCHAR(64),
  tit VARCHAR(256),
  intr VARCHAR(128),
  listened VARCHAR(16)
);

